package vn.edu.fit.iuh.repositories;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import vn.edu.fit.iuh.enities.Candidate;import vn.edu.fit.iuh.enities.Company;import vn.edu.fit.iuh.enities.Job;import java.util.List;@Repositorypublic interface CompanyRepository extends JpaRepository<Company, Long> {    @Query("SELECT c FROM Company c LEFT JOIN FETCH c.address")    List<Company> findAllCompanyWithAddress();}